html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% set data = json.loads(doc.custom_operation_form_data or "{}") %}
{% if letter_head and not no_letterhead -%}
{% if print_settings.repeat_header_footer %}
<div class="letter-head">{{ letter_head }}</div>
{%- endif %}{%- endif %}

<main style="font-size: 12px;">
<style>
    th {
        text-align: center;
    }
    table td.details-col, table th.details-col {
        text-align: left;
    }
</style>


<p><b>Assembly</b></p>
<br>

<table border="1" width="100%" style="font-size: 12px; text-align: center; border-collapse: collapse;">
    <tr>
        <th>Sr. No.</th>
        <th>Stage</th>
        <th>Line Clearance Criteria</th>
        <th>Yes/No</th>
        <th>Sign/Date</th>
    </tr>
    {% if doc.custom_line_clearance_checklist_details %}
        {% for row in doc.custom_line_clearance_checklist_details %}
            <tr>
                {% if loop.first %}
                    <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">1</td>
                    <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">{{ doc.operation or '' }}</td>
                {% endif %}
                <td style="text-align: left; padding: 4px;">{{ row.line_clearance_checklist or '' }}</td>
                <td>{{ row.yesno or '' }}</td>
                {% if loop.first %}
                    <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}"></td>
                {% endif %}
            </tr>
        {% endfor %}
    {% else %}
        <tr>
            <td>1</td>
            <td>{{ doc.operation or '' }}</td>
            <td colspan="3">No criteria defined</td>
        </tr>
    {% endif %}
</table>


<br>
<p><b>Remarks:</b>______________________________________</p>
<br>
<p><b>Sub Assembly:</b></p>
<br>
<p><b>Sub Assembly performed by production:</b></p>

<table border="1" width="100%" style="font-size: 12px; text-align: center;">
    <tr>
        <th>Sr. No.</th>
        <th>Part No</th>
        <th>Hardware Components</th>
        <th>Assembled Yes/No</th>
        <th>Start Date</th>
        <th>End Date</th>
        <th>Remark</th>
    </tr>
    {% for row in doc.custom_jobcard_opeartion_deatils %}
    <tr>
        <td>{{ loop.index }}</td>
        <td style="white-space: nowrap">{{ row.item_code or '' }}</td>
        <td>{{ row.item_name or '' }}</td>
        <td>{{ row.verified or '' }}</td>
        {% set startdate = doc.time_logs %}
        <td style="white-space: nowrap">{% if startdate and startdate[0].from_time %} {{ frappe.utils.format_datetime(startdate[0].from_time, "dd-MM-yyyy") or '' }} {% endif %}</td>
        {% set enddate = doc.time_logs %}
        <td style="white-space: nowrap">{% if enddate and enddate[-1].to_time %} {{ frappe.utils.format_datetime(enddate[-1].to_time, "dd-MM-yyyy") or '' }} {% endif %}</td>
        <td>{{ row.remarks or '-' }}</td>
    </tr>
    {% endfor %}
</table>

<br>
<p><b>Remarks:</b>______________________________________</p>
<br>
