html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% set data = json.loads(doc.custom_operation_form_data or "{}") %}
{% if letter_head and not no_letterhead -%}
{% if print_settings.repeat_header_footer %}
<div class="letter-head">{{ letter_head }}</div>
{%- endif %}{%- endif %}

{% set sign = frappe.get_doc("Employee", doc.custom_signed_by) if doc.custom_signed_by else {}  %}
{% set verified = frappe.get_doc("Employee", doc.custom_authorised_by) if doc.custom_authorised_by else {} %}

<main style="font-size: 12px;">
<style>
    th {
        text-align: center;
    }
    table td.details-col, table th.details-col {
        text-align: left;
    }
</style>


<p><b>List of Feasibility Testing:</b></p>

<table border="1" width="100%" style="font-size: 12px; text-align: center;">
    <tr>
        <th>Sr. No.</th>
        <th>Feasibility Testing</th>
        <th>Verified</th>
        <th>Checked by</th>
        <th>Remarks</th>
    </tr>
    {% for row in doc.custom_feasibility_testing %}
    <tr>
        <td>{{ loop.index }}</td>
        <td>{{ row.feasibility_testing or '' }}</td>
        <td>{{ row.verified or ''}}</td>
        <td>{{ sign.employee_name or  "" }}</td>
        <td>{{ row.remarks or '-'}}</td>
    </tr>
    {% endfor %}
</table>

<br>
<p><b>Remarks:</b>______________________________________</p>
<br>

<table width="100%" style="font-size: 12px;">
  <tr>
        <td><b>Checked By:</b>{{ sign.employee_name or  "" }} ({{ doc.custom_signed_by or  "_______________________" }})</td>
        <td><b>Verified By:</b>{{ verified.employee_name or  "" }} ({{ doc.custom_authorised_by or  "_______________________" }})</td>
  </tr>

</table>
</main>
