html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% set data = json.loads(doc.custom_operation_form_data or "{}") %}
{% set sign = frappe.get_doc("Employee", doc.custom_signed_by) if doc.custom_signed_by else {}  %}
{% set verified = frappe.get_doc("Employee", doc.custom_authorised_by) if doc.custom_authorised_by else {} %}
{% if letter_head and not no_letterhead -%}
{% if print_settings.repeat_header_footer %}
<div class="letter-head">{{ letter_head }}</div>
{%- endif %}{%- endif %}

{% set attachments = frappe.get_all(
    "File",
    filters={"attached_to_doctype": doc.doctype, "attached_to_name": doc.name},
    fields=["file_url", "file_name"]
) %}




<main style="font-size: 12px;">
<style>
    th {
        text-align: center;
    }
    table td.details-col, table th.details-col {
        text-align: left;
    }
</style>


<p><b>Label</b></p>
<br>

<table border="1" width="100%" style="font-size: 12px; text-align: center; border-collapse: collapse;">
    <tr>
        <th>Sr. No.</th>
        <th>Stage</th>
        <th>Line Clearance Criteria</th>
        <th>Yes/No</th>
        <th>Sign/Date</th>
    </tr>
    {% if doc.custom_line_clearance_checklist_details %}
        {% for row in doc.custom_line_clearance_checklist_details %}
            <tr>
                {% if loop.first %}
                    <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">1</td>
                    <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">{{ doc.operation or '' }}</td>
                {% endif %}
                <td style="text-align: left; padding: 4px;">{{ row.line_clearance_checklist or '' }}</td>
                <td>{{ row.yesno or '' }}</td>
                {% if loop.first %}
                    <td style="vertical-align: middle !important;" rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">{{ sign.employee_name or "" }} ({{ doc.custom_signed_by or '-'}})</td>
                {% endif %}
            </tr>
                            {% endfor %}

    {% else %}
        <tr>
            <td>1</td>
            <td>{{ doc.operation or '' }}</td>
            <td colspan="3">No criteria defined</td>
        </tr>
    {% endif %}
</table>

<br>
<p><b>Label 1: Robot cart label</b></p>
{% if attachments and attachments|length > 0 %}
    {% set att = attachments[0] %}
    {% if att.file_url.lower().endswith(('.png', '.jpg', '.jpeg', '.gif')) %}
        <div style="border: 1px solid #ccc; padding: 5px; display:inline-block;">
            <img src="{{ att.file_url }}" style="max-width:200px; max-height:200px;">
        </div>
    {% endif %}
{% endif %}
<br>

<p><b>Label 2: Vision cart label</b></p>
{% if attachments|length > 1 %}
    {% set att = attachments[1] %}
    {% if att.file_url.lower().endswith(('.png', '.jpg', '.jpeg', '.gif')) %}
        <div style="border: 1px solid #ccc; padding: 5px; display:inline-block;">
            <img src="{{ att.file_url }}" style="max-width:200px; max-height:200px;">
        </div>
    {% endif %}
{% endif %}
<br>

<p><b>Label 3: Pre Planner</b></p>
{% if attachments|length > 2 %}
    {% set att = attachments[2] %}
    {% if att.file_url.lower().endswith(('.png', '.jpg', '.jpeg', '.gif')) %}
        <div style="border: 1px solid #ccc; padding: 5px; display:inline-block;">
            <img src="{{ att.file_url }}" style="max-width:200px; max-height:200px;">
        </div>
    {% endif %}
{% endif %}
<br>
<br>
<p><b>Label 4: System Label </b></p>
{% if attachments|length > 3 %}
    {% set att = attachments[3] %}
    {% if att.file_url.lower().endswith(('.png', '.jpg', '.jpeg', '.gif')) %}
        <div style="border: 1px solid #ccc; padding: 5px; display:inline-block;">
            <img src="{{ att.file_url }}" style="max-width:200px; max-height:200px;">
        </div>
    {% endif %}
{% endif %}
<br>
<br>


        <div style="float:left"><b>Checked By:{{ sign.employee_name or "" }} ({{ doc.custom_signed_by or '-'}})</b></div>
        <div style="float:right"><b>Verified By:{{ verified.employee_name or "" }} ({{ doc.custom_authorised_by or '-'}})</b></div>
            <br>    
        <div style="float: left;" width="50%"><b>Date: </b>{{ doc.custom_signed_by_date or "" }}</div>
        <div style="float: right;"><b>Date: </b>{{ doc.custom_authorised_by_date or ""  }}</div>

</main>
