html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% set data = json.loads(doc.custom_operation_form_data or "{}") %}
{% set sign = frappe.get_doc("Employee", doc.custom_signed_by) if doc.custom_signed_by else {}  %}
{% set verified = frappe.get_doc("Employee", doc.custom_authorised_by) if doc.custom_authorised_by else {} %}
{% if letter_head and not no_letterhead -%}
{% if print_settings.repeat_header_footer %}
<div class="letter-head">{{ letter_head }}</div>
{%- endif %}{%- endif %}

{% set attachments = frappe.get_all(
    "File",
    filters={"attached_to_doctype": doc.doctype, "attached_to_name": doc.name},
    fields=["file_url", "file_name"]
) %}



<main style="font-size: 12px;">
<style>
    th {
        text-align: center;
    }
    table td.details-col, table th.details-col {
        text-align: left;
    }
</style>

<table style="width:100%; border:0; border-collapse:collapse;"> 
        <tr> <!-- Left: Logo --> 
             <td style="width:20%; text-align:left; vertical-align:middle;"></td>
             <!-- Middle: Title --> 
             <td style="width:60%; text-align:left; vertical-align:middle;"></td> 
             <!-- Right: Format Info --> 
             <td style="width:20%; text-align:center; font-size:10px; vertical-align:top;">
                  <b>Document No.</b><br> <b>PR/FMT/RS/004 Rev-00</b>
             </td> 
        </tr> 
</table>

<p><b>Label</b></p>
<br>

<table border="1" width="100%" style="font-size: 12px; text-align: center; border-collapse: collapse;">
    <tr>
        <th>Sr. No.</th>
        <th>Stage</th>
        <th>Line Clearance Criteria</th>
        <th>Yes/No</th>
        <th>Sign/Date</th>
    </tr>
    {% if doc.custom_line_clearance_checklist_details %}
        {% for row in doc.custom_line_clearance_checklist_details %}
            <tr>
                {% if loop.first %}
                    <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">1</td>
                    <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">{{ doc.operation or '' }}</td>
                {% endif %}
                <td style="text-align: left; padding: 4px;">{{ row.line_clearance_checklist or '' }}</td>
                <td>{{ row.yesno or '' }}</td>
                {% if loop.first %}
                    <td style="vertical-align: middle !important;" rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">{{ sign.employee_name or "" }} ({{ doc.custom_signed_by or '-'}}) - {{ frappe.utils.format_datetime(doc.custom_signed_by_date, "dd-MM-yyyy") or '' }}</td>
                {% endif %}
            </tr>
                            {% endfor %}

    {% else %}
        <tr>
            <td>1</td>
            <td>{{ doc.operation or '' }}</td>
            <td colspan="3">No criteria defined</td>
        </tr>
    {% endif %}
</table>

<br>
{% set labels = [
    "Label 1: Robot cart label",
    "Label 2: Vision cart label",
    "Label 3: Pre Planner",
    "Label 4: System Label"
] %}

{% for i in range(0, doc.custom_job_card_attachments|length) %}
    {% set row = doc.custom_job_card_attachments[i] %}

    <p><b>{{ labels[i] }}</b></p>

    {% if row.attach and row.attach.lower().endswith(('.png', '.jpg', '.jpeg', '.gif')) %}
        <div style="border: 1px solid #ccc; padding: 5px; display:inline-block; margin: 5px;">
            <img src="{{ row.attach }}" style="max-width:200px; max-height:200px;">
        </div>
    {% endif %}
    <br><br>
{% endfor %}

<br>
<br>

<div>
        <div style="float:left; white-space:nowrap;"><b>Checked By:</b> {{ sign.employee_name or "" }} ({{ doc.custom_signed_by or '-'}}) <br>
        <b>Date: </b>{{ frappe.utils.format_datetime(doc.custom_signed_by_date, "dd-MM-yyyy") or '' }}
        </div>
        <div style="float:right; white-space:nowrap;"><b>Verified By:</b> {{ verified.employee_name or "" }} ({{ doc.custom_authorised_by or '-'}})<br>
        <b>Date: </b>{{ frappe.utils.format_datetime(doc.custom_authorised_by_date, "dd-MM-yyyy") or '' }}</div>
</div>
</main>
