html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% set data = json.loads(doc.custom_operation_form_data or "{}") %}
{% if letter_head and not no_letterhead -%}
{% if print_settings.repeat_header_footer %}
<div class="letter-head">{{ letter_head }}</div>
{%- endif %}{%- endif %}

{% set sign = frappe.get_doc("Employee", doc.custom_signed_by) if doc.custom_signed_by else {}  %}
{% set verified = frappe.get_doc("Employee", doc.custom_authorised_by) if doc.custom_authorised_by else {} %}

<main style="font-size: 12px;">
<style>
    table {
        border-collapse: collapse;
        width: 100%;
        font-size: 12px;
        text-align: center;
    }
    th, td {
        border: 1px solid #000;
        padding: 4px;
    }
    th {
        background: #f5f5f5;
        text-align: center;
    }
    td.details-col, th.details-col {
        text-align: left;
    }
    .remarks {
        margin: 8px 0;
    }
</style>

<table style="width:100%; border:0; border-collapse:collapse;">
    <tr>
        <!-- Left: Logo -->
        <td style="width:20%; text-align:left; vertical-align:middle; border:0 !important;"></td>

        <!-- Middle: Title -->
        <td style="width:60%; text-align:left; vertical-align:middle; border:0 !important;"></td>

        <!-- Right: Format Info -->
        <td style="width:20%; text-align:center; font-size:10px; vertical-align:top; border:0 !important;">
            <b>Document No.</b><br> 
            <b>{{ doc.custom_document_no or "PR/FMT/RS/003 Rev-00" }}</b>
        </td>
    </tr>
</table>

<p><b>Software Installation</b></p>
<br>

<table>
    <tr>
        <th>Sr. No.</th>
        <th>Stage</th>
        <th>Line Clearance Criteria</th>
        <th>Yes/No</th>
        <th>Sign/Date</th>
    </tr>
    {% if doc.custom_line_clearance_checklist_details %}
        {% for row in doc.custom_line_clearance_checklist_details %}
        <tr>
            {% if loop.first %}
                <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">1</td>
                <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">{{ doc.operation or '' }}</td>
            {% endif %}
            <td class="details-col">{{ row.line_clearance_checklist or '' }}</td>
            <td>{{ row.yesno or '' }}</td>
            {% if loop.first %}
                <td rowspan="{{ doc.custom_line_clearance_checklist_details | length }}">{{ sign.employee_name or  "" }} ({{ doc.custom_signed_by or  "-" }}) - {{ frappe.utils.format_datetime(doc.custom_signed_by_date, "dd-MM-yyyy") or '' }}</td>
            {% endif %}
        </tr>
        {% endfor %}
    {% else %}
        <tr>
            <td>1</td>
            <td>{{ doc.operation or '' }}</td>
            <td colspan="3">No criteria defined</td>
        </tr>
    {% endif %}
</table>
<br>
<!-- <p class="remarks"><b>Remarks:</b> ______________________________________</p> --->
<br>
<p><b>Software to be Installed:</b></p>
<table>
    <tr>
        <th>Sr. No.</th>
        <th>Software</th>
        <th>Version</th>
        <th>Installed Yes/No</th>
    </tr>
    <tr>
        <td>1</td>
        <td>{{ doc.custom_software or '' }}</td>
        <td>{{ doc.custom_version or '' }}</td>
        <td>{{ doc.custom_installed or '' }}</td>
    </tr>
</table>
<br>
<!-- <p class="remarks"><b>Remarks:</b> ______________________________________</p> -->
<br>


       <div>
        <div style="float:left; white-space:nowrap;"><b>Checked By:</b> {{ sign.employee_name or "" }} ({{ doc.custom_signed_by or '-'}}) <br>
        <b>Date: </b>{{ frappe.utils.format_datetime(doc.custom_signed_by_date, "dd-MM-yyyy") or '' }}
        </div>
        <div style="float:right; white-space:nowrap;"><b>Verified By:</b> {{ verified.employee_name or "" }} ({{ doc.custom_authorised_by or '-'}})<br>
        <b>Date: </b>{{ frappe.utils.format_datetime(doc.custom_authorised_by_date, "dd-MM-yyyy") or '' }}</div>
</div>


<br>

</main>
