html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% set data = json.loads(doc.custom_operation_form_data or "{}") %}
{% if letter_head and not no_letterhead -%}
{% if print_settings.repeat_header_footer %}
<div class="letter-head">{{ letter_head }}</div>
{%- endif %}{%- endif %}

{% set inspection = frappe.get_doc("Employee", doc.custom_inspection_done_by) if doc.custom_inspection_done_by else {}  %}
{% set verified = frappe.get_doc("Employee", doc.custom_verification_done_by) if doc.custom_verification_done_by else {} %}

<main style="font-size: 12px;">
<style>
    th {
        text-align: center;
    }
    table td.details-col, table th.details-col {
        text-align: left;
    }
</style>



<p><b>Sub Assembly Inspection by QC:</b></p>

<table border="1" width="100%" style="font-size: 12px; text-align: center;">
    <tr>
        <th>Sr. No.</th>
        <th>Part No</th>
        <th>Hardware Components</th>
        <th>Verified</th>
        <th>Remark</th>
    </tr>
    {% for i in doc.custom_qc_items %}
    <tr>
        <td>{{ loop.index }}</td>
        <td>{{ i.item_code or '' }}</td>
        <td>{{ i.item_name or '' }}</td>
        <td>{{ i.verified or '' }}</td>
        <td>{{ i.remarks or '-' }}</td>
    </tr>
    {% endfor %}
</table>

<br>


<br>

        <div style="float:left;">
            <b>Checked By: {{inspection.employee_name or '' }} ({{ frappe.utils.format_datetime(doc.custom_inspection_date, "dd-MM-yyyy") or '' }})</b>
        </div>
        <div style="float:right;">
            <b>Verified By:{{verified.employee_name or '' }} ({{ frappe.utils.format_datetime(doc.custom_verification_date, "dd-MM-yyyy") or '' }})</b>
        </div>

<br>
