html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% set data = json.loads(doc.custom_operation_form_data or "{}") %}
{% if letter_head and not no_letterhead -%}
{% if print_settings.repeat_header_footer %}
<div class="letter-head">{{ letter_head }}</div>
{%- endif %}{%- endif %}

<main style="font-size: 12px;">
<style>
    table {
        border-collapse: collapse;
        width: 100%;
        font-size: 12px;
        text-align: center;
    }
    th, td {
        border: 1px solid #000;
        padding: 4px;
    }
    th {
        background: #f5f5f5;
    }
    td.details-col, th.details-col {
        text-align: left;
    }
    .remarks {
        margin: 8px 0;
    }
</style>

<!-- (B) QC for Software Installation -->
<p><b>(B) QC for Software Installation:</b></p>
<table>
    <tr>
        <th>Sr. No.</th>
        <th>Software</th>
        <th>Verified</th>
        <th>Remarks</th>
    </tr>
     {% for i in doc.custom_qc_items %}
    <tr>
        <td>{{ loop.index }}</td>
        <td>{{ i.item_name or '' }}</td>
        <td>{{ i.verified or '' }}</td>
        <td>{{ i.remarks or '-' }}</td>
    </tr>
    {% endfor %}
</table>
<br>
<p><b>Remarks:</b>______________________________________</p>
<br>

<table width="100%" style="font-size: 12px;">
  <tr>
        <td><b>Checked By: {{doc.inspected_by or '' }}({{ frappe.utils.format_datetime(doc.report_date, "dd-MM-yyyy") or '' }})</b></td>
        <td><b>Verified By:{{doc.verified_by or '' }}({{ frappe.utils.format_datetime(doc.report_date, "dd-MM-yyyy") or '' }})</b></td>
  </tr>

</table>

</main>
