html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field -%}
{% set data = json.loads(doc.custom_operation_form_data or "{}") %}
{% if letter_head and not no_letterhead -%}
{% if print_settings.repeat_header_footer %}
<div class="letter-head">{{ letter_head }}</div>
{%- endif %}{%- endif %}

{% set inspection = frappe.get_doc("Employee", doc.custom_inspection_done_by) if doc.custom_inspection_done_by else {}  %}
{% set verified = frappe.get_doc("Employee", doc.custom_verification_done_by) if doc.custom_verification_done_by else {} %}

<main style="font-size: 12px;">
<style>
    table {
        border-collapse: collapse;
        width: 100%;
        font-size: 12px;
        text-align: center;
    }
    th, td {
        border: 1px solid #000;
        padding: 4px;
    }
    th {
        background: #f5f5f5;
    }
    td.details-col, th.details-col {
        text-align: center;
    }
    .remarks {
        margin: 8px 0;
    }
</style>

<p><b>QC for Software Installation:</b></p>
<table>
    <tr>
        <th style="text-align: center;">Sr. No.</th>
        <th style="text-align: center;">Software</th>
        <th style="text-align: center;">Verified</th>
        <th style="text-align: center;">Remarks</th>
    </tr>
    <tr>
        <td>1</td>
        <td>{{ doc.custom_software or '' }}</td>
        <td>{{ doc.custom_verified or '' }}</td>
        <td>{{ doc.remarks or '-' }}</td>
    </tr>
</table>

<br>

        <div style="float:left; white-space:nowrap">
            <b>Checked By:</b> {{inspection.employee_name or '' }} ({{inspection.name or '' }})<br>
            <b>Date:</b> {{ frappe.utils.format_datetime(doc.custom_inspection_date, "dd-MM-yyyy") or '' }}
        </div>
        <div style="float:right; white-space:nowrap">
            <b>Verified By:</b> {{verified.employee_name or '' }} ({{verified.name or '' }})<br>
            <b>Date:</b> {{ frappe.utils.format_datetime(doc.custom_verification_date, "dd-MM-yyyy") or '' }}
        </div>

<br>

</main>
